js:
require(config.模板);
let host = MY_HOME;
let cate_url = 'http://video-static.czjsbo.com/static/1/config/lib-new.json';
var cate_data = JSON.parse(request(cate_url)).data;
var d = [];
var page = MY_PAGE;
//var html=getResCode();
MY_URL = MY_URL.split('##')[1];
//var pageSize=MY_URL.match(/pageSize=(\d+)/)[1];
true_url = getMyVar('header.url', MY_URL);
if (page > 1) {
    true_url = true_url.replace(/(\d+)\.json/, page + '.json');
}
//log(true_url);
//var html=request(true_url);
let html = fetch(true_url, {
    headers: {
        versionnumber: '330',
        versionname: '3.3.0',
        device: 'c5e72f6ad591a24ebd6c33162d8a3c1a',
        appid: '1',
        platformid: '7',
        'User-Agent': 'okhttp/3.14.7',
    }
});
//log(html)
let data = JSON.parse(html).data.list;
var listdata = data.map((it) => {
    return {
        title: it.vodName,
        pic_url: host + it.vodPicThumb,
        //pic_url:host+it.vodPic,
        desc: it.lastName,
        url: 'hiker://empty##' + host + '/static/video/detail/' + it.id + '.json#immersiveTheme#'
        //url: 'hiker://empty##' + host + '/api/v1/hisotryDetail?vodId=' + it.id + '#immersiveTheme#'
    }

});
//let turl='https://video-api.czjsbo.com/videolibrary/v2/1/fyclass/fyarea/fytype/2/-1/fyyear/-1/'+page+'.json?pageSize='+pageSize;
let fyclass = true_url.match(/v2\/(.*?)\/(.*?)\//)[2];
//log(fyclass);
var sec = [];
var thi = [];
if (fyclass == '28') {
    sec = cate_data.types[0].children;
    thi = cate_data.types[0].tags;
} else if (fyclass == '60') {
    sec = cate_data.types[1].children;
    thi = cate_data.types[1].tags;
} else if (fyclass == '34') {
    sec = cate_data.types[2].children;
    thi = cate_data.types[2].tags;
} else if (fyclass == '33') {
    sec = cate_data.types[3].children;
    thi = cate_data.types[3].tags;
}

let cates = 打造动态分类([{
        一级分类: '@js:' + $.toString((fisrtCate) => {
            return [cate_data]
        }, cate_data),
        子分类: '@js:' + $.toString(() => {
            let ret = input.types;
            /*
            ret.unshift({
                typeName:'全部',
                typeId:-1
            });
            */
            return ret
        }),
        分类标题: '@js:' + $.toString(() => {
            return input.typeName
        }),
        分类链接: '@js:' + $.toString((MY_URL) => {
            return MY_URL.replace(/v2\/(.*?)\/(.*?)\//, 'v2/$1/' + input.typeId + '/');
        }, MY_URL),
    },
    {
        一级分类: '@js:' + $.toString((fisrtCate) => {
            return [sec]
        }, sec),
        子分类: '@js:' + $.toString(() => {
            input.unshift({
                typeName: '全部',
                typeId: -1
            });
            return input
        }),
        分类标题: '@js:' + $.toString(() => {
            return input.typeName
        }),
        分类链接: '@js:' + $.toString((true_url) => {
            true_url = true_url.replace(/v2\/(.*?)\/(.*?)\/(.*?)\//, 'v2/$1/$2/' + input.typeId + '/');
            return true_url
        }, true_url),
    },
    {
        一级分类: '@js:' + $.toString((fisrtCate) => {
            return [thi]
        }, thi),
        子分类: '@js:' + $.toString(() => {
            input.unshift({
                typeName: '全部',
                typeId: -1
            });
            return input
        }),
        分类标题: '@js:' + $.toString(() => {
            return input.typeName
        }),
        分类链接: '@js:' + $.toString((true_url) => {
            true_url = true_url.replace(/v2\/(.*?)\/(.*?)\/(.*?)\/(.*?)\//, 'v2/$1/$2/$3/' + input.typeId + '/');
            return true_url
        }, true_url),
    },
    {
        一级分类: '@js:' + $.toString((cate_data) => {
            return [cate_data]
        }, cate_data),
        子分类: '@js:' + $.toString(() => {
            //log(input);
            input = input.year;
            input.unshift('-1');
            return input
        }),
        分类标题: '@js:' + $.toString(() => {
            return input.replace('-1', '全部')
        }),
        分类链接: '@js:' + $.toString((true_url) => {
            true_url = true_url.replace(/v2\/(.*?)\/(.*?)\/(.*?)\/(.*?)\/(.*?)\/(.*?)\/(.*?)\//, 'v2/$1/$2/$3/$4/$5/$6/' + input + '/');
            return true_url
            //return turl.replace('fyyear',input)
        }, true_url),
    }
]);
设置(cates);
d = d.concat(cates);
d = d.concat(listdata);
setResult(d)

