    title: detail.vodName + '\n' + detail.vodUpdateTip + '\n' + detail.typeName,
    desc: detail.vodPubdate + '\n' + detail.vodYear + '\n' + detail.vodArea,
    url: host + detail.vodPic,
    pic_url: host + detail.vodPic,
    col_type: 'movie_1_vertical_pic_blur',
});

try {
    var info = detail.vodBlurb;
} catch (e) {
    var info = '暂无简介'
