function getRandomArrayValue(arr, num) {
    var sData = arr.slice(0),
        i = arr.length,
        min = i - num,
        item, index;
    while (i-- > min) {
        index = Math.floor((i + 1) * Math.random());
        item = sData[index];
        sData[index] = sData[i];
        sData[i] = item;
    }
    return sData.slice(min);
}

