if (ÈÄâÈõÜÊï∞ÁªÑ.length > 110) {
    //ËÆæÁΩÆÊØèÂå∫ÈÄâÈõÜÊï∞ÁõÆ
    var page_number = 100;
    var star = getMyVar('ÂàÜÈõÜËµ∑', '1');
    var end = getMyVar('ÂàÜÈõÜÁªà', JSON.stringify(page_number));
    var total = Math.ceil(ÈÄâÈõÜÊï∞ÁªÑ.length / page_number);
    var catalogue = []
    for (let i = 0; i < total; i++) {
        catalogue += i * page_number + ',';
        catalogue = catalogue.split(',');
    }
    for (let i = 0; i < 8; i++) {
        d.push({
