        if (i == (catalogue.length - 2)) var total2 = ÈÄâÈõÜÊï∞ÁªÑ.length;
        d.push({
            title: star == total1 ? '‚Äò‚Äò' + total1 + '-' + total2 + '‚Äô‚Äô' : total1 + '-' + total2,
            url: $("#noLoading#").lazyRule((total1, total2) => {
                putMyVar('ÂàÜÈõÜËµ∑', total1);
                putMyVar('ÂàÜÈõÜÁªà', total2);
                refreshPage(false);
                return 'hiker://empty'
            }, total1, total2),
            col_type: 'scroll_button'
        });
    }
    for (var j = star - 1; j < end; j++) {
        playLists()
    }
    d.push({
        col_type: "line"
    })
    var yema = Math.ceil(end / page_number);
    d.push({
        title: "‚Äú‚Äú‚Äù‚Äù <small><small><font color='grey'>" +
            yema + ' / ' + total +
            "</font> </small></small>",
        col_type: "text_center_1",
        extra: {
            lineVisible: false
        }
    })
}
setResult(d)

